INSERT INTO `services` (`id`, `icon`, `title`, `description`, `created_at`, `updated_at`, `link`, `image`, `slug`, `body`) VALUES
(1, '/storage/services/vector-04-01.png', 'Xây dựng và phát triển website', 'Chúng tôi cung cấp Thiết kế UX/UI tốt nhất bằng cách theo các xu hướng mới nhất của thị trường', '2020-07-03 21:27:00', '2020-08-07 03:08:46', '/xay-dung-va-phat-trien-website', 'services/August2020/Ufgiv0Mu0o0owQx1014e.png', NULL, '<p style=\"margin: 0px; color: #515050; text-align: justify; overflow: hidden; padding-top: 10px; font-size: 16px !important; font-family: Roboto-Regular !important;\"><span style=\"color: #292b2c; font-family: \'Lora\'; font-size: 16px; text-align: start;\">Website kh&ocirc;ng c&ograve;n l&agrave; c&ocirc;ng cụ xa lạ của c&aacute;c doanh nghiệp, c&aacute;c tổ chức hay thậm ch&iacute; l&agrave; c&aacute;c c&aacute; nh&acirc;n bởi hầu hết với sự ph&aacute;t triển của internet mỗi đối tượng đều sở hữu cho m&igrave;nh một website. Tuy nhi&ecirc;n,&nbsp;</span><span style=\"box-sizing: inherit; font-weight: bolder; color: #292b2c; font-family: \'Lora\'; font-size: 16px; text-align: start;\">website l&agrave; g&igrave;</span><span style=\"color: #292b2c; font-family: \'Lora\'; font-size: 16px; text-align: start;\"> v&agrave; c&oacute; tầm quan trọng như thế n&agrave;o th&igrave; kh&ocirc;ng phải ai cũng c&oacute; được một đ&aacute;p &aacute;n đ&uacute;ng. </span></p>\r\n<h2 style=\"margin: 0px; color: #515050; text-align: justify; overflow: hidden; padding-top: 10px; font-size: 16px !important; font-family: Roboto-Regular !important;\"><span style=\"color: #292b2c; font-family: -apple-system, system-ui, BlinkMacSystemFont, Segoe UI, Roboto, Helvetica Neue, Arial, sans-serif;\"><span style=\"font-size: 16px;\">Website l&agrave; g&igrave;?</span></span></h2>\r\n<p style=\"margin: 0px; color: #515050; text-align: justify; overflow: hidden; padding-top: 10px; font-size: 16px !important; font-family: Roboto-Regular !important;\"><span style=\"color: #292b2c; font-family: \'Lora\'; font-size: 16px;\">Website l&agrave; một trang th&ocirc;ng tin với mục đ&iacute;ch l&agrave;